          tooltipDiv = d3.select('#'+treesvgid)
          .select('#g-level-1')
          .append('g')
            .attr('class', 'tooltip')
            .attr("transform", `translate(${pD.x + 6},${pD.y - 10})`)
        }
        else if(treesvgid == 'radial_tree' || treesvgid == 'radial_dendrogram_tree'){
          tooltipDiv = d3.select('#'+treesvgid)
          .select('#g-level-1')
          .append('g')
            .attr('class', 'tooltip')
            .attr("transform", `rotate(${pD.x * 180 / Math.PI - 90}) translate(${pD.y},0) rotate(${360 - (pD.x * 180 / Math.PI - 90)})`)
        }
        else if(treesvgid == 'icicleplot'){
          tooltipDiv = d3.select('#'+treesvgid)
          .select('#g-level-1')
          .append('g')
            .attr('class', 'tooltip')
            .attr("transform", `translate(${pD.x0 + 10},${pD.y0})`)
        }
