            .attr("font-size", '0.5em')
            .attr("text-anchor", "start")
          .append('tspan')
            .attr("dy",d=>`${10 + 15 * d.index}`)
            .attr("dx", 10)
            .attr("fill","#d6d4d4")
            .text(d=>`${d.index==0 ? name : d.name} : ${d.index==0 ? d.name : pD.data.data[d.name]}`)
          )
