      type: Object
    }
  },
  data() {
    return {
      width:  0,
      height: 0,
      left: 0,
      top,
      nodes: [],
      links: [],
      root: null,
      treeData: 
        {
          "name": "Top Level",
          "parent": "null",
          "children": [
            {
              "name": "Level 2: A",
              "parent": "Top Level",
              "children": [
                {
