                }
              ]
            }
          ]
        }

    }
  },
  mounted() {
    console.log('hierarchicalData', this.hierarchicalData)
    this.initTreeLayout()
    this.renderNodeLinkTree()
  },
  watch: {
      displayMode: function() {
        console.log('displayMode')
      }
  },
  computed: {
    ...mapState([
      'displayMode'
    ]),
    gTransform() {
      return `translate(350, 20)`
    },
  },
  methods: {
    initTreeLayout() {
      console.log('initTreeLayout')
    },
    computeLinkKey(link) {
      let thisNode = link
      let thisParentNode = link.parent
      return 'x' + thisParentNode.x + '-y' + thisParentNode.y + '=' + 'x' + thisNode.x + '-y' + thisNode.y
    },
    computeNodeKey(node) {
      let nodeFill = node
      let nodeCollapsed = node.children==null?1:0
      return 'x' + node.x + '-y' + node.y + '-' + nodeCollapsed
    },
    renderNodeLinkTree() {

      // render the node link tree visualization using d3
      var treeData = this.hierarchicalData

      // Set the dimensions and margins of the diagram
      var margin = {top: 20, right: 90, bottom: 30, left: 90},
          width = 100 - margin.left - margin.right,
          height = 800 - margin.top - margin.bottom;

      this.width = width + margin.right + margin.left
      this.height = height + margin.top + margin.bottom
      this.left = 90
      this.top = 20

      // declares a tree layout and assigns the size
      var treemap = d3.tree().size([this.height, this.width]);

      // Assigns parent, children, height, depth
      this.root = d3.hierarchy(treeData, function(d) { return d.children; });
      console.log('root:', this.root)
      console.log('height', this.height)
      this.root.x0 = this.height / 2;
      this.root.y0 = 0;

      var treeData = treemap(this.root);

      // Compute the new tree layout.
      var nodes = treeData.descendants(),
          links = treeData.descendants().slice(1);

      // Normalize for fixed-depth.
      nodes.forEach(function(d){ d.y = d.depth * 65});
      this.nodes = nodes
      this.links = links

    },
    updateTree(d) {
        if (d.children) {
          d._children = d.children;
          d.children = null;
        } else {
         d.children = d._children;
         d._children = null;
        }
        console.log("updateRoot:",this.root)
        var treemap = d3.tree().size([this.height, this.width]);
        var treeData = treemap(this.root);

        // Compute the new tree layout.
        var nodes = treeData.descendants(),
            links = treeData.descendants().slice(1);

        // Normalize for fixed-depth.
        nodes.forEach(function(d){ d.y = d.depth * 65});
        this.nodes = nodes
        this.links = links
        console.log("updateLink: ", links)
        console.log("updateNode: ", nodes)

    }
  }
}
