result:
.re-node {
  height: 100%;
  width: 30%;

}
path {
    stroke: #000000;
    stroke-width: 2px;
    fill: none;
}
.node-rect {
    stroke: #c4bbbb;
    stroke-width: 2;
    stroke-dasharray: 4 2;
    fill: rgb(255, 255, 255);
}
.node-rect-opacity{
    opacity: 0;
    cursor: pointer;
}
.upper-rect {
    stroke: #c4bbbb;
    stroke-width: 2;
    stroke-dasharray: 2 1.67 3 1.67 3 1.67 2 0.0001;
    fill: white;
}
.repeat-rect{
    opacity: 0;
}
